mapper.sql:
SELECT COUNT( * ) FROM `exam_student_scores`

     and exam_id = #{examId}
     and grade = #{grade}
     and classes = #{classes}

mapper.select:
SELECT
    a.*,(
    @i := @i + 1
    ) AS muitiple_rank
FROM
    ( SELECT exam_statistics_id FROM exam_class_statictics WHERE exam_id = #{examId} AND grade = #{grade} AND `subject` = #{subject} ORDER BY muitiple_score DESC ) a,(
    SELECT
        @i := 0
) b

SELECT
    a.*,(
    @i := @i + 1
    ) AS average_rank
FROM
    ( SELECT exam_statistics_id FROM exam_class_statictics WHERE exam_id = #{examId} AND grade = #{grade} AND `subject` = #{subject} ORDER BY average_score DESC ) a,(
    SELECT
        @i := 0
) b

mapper.update:
update exam_class_statictics
grade = #{grade}, classes = #{classes}, subject = #{subject}, exam_numbers = #{examNumbers}, full_socre_numbers = #{fullSocreNumbers}, excellent_numbers = #{excellentNumbers}, good_numbers = #{goodNumbers}, qualified_numbers = #{qualifiedNumbers}, unqualified_one_numbers = #{unqualifiedOneNumbers}, unqualified_two_numbers = #{unqualifiedTwoNumbers}, unqualified_three_numbers = #{unqualifiedThreeNumbers}, unqualified_four_numbers = #{unqualifiedFourNumbers}, total_score = #{totalScore}, average_score = #{averageScore}, qualified_percentage = #{qualifiedPercentage}, excellent_percentage = #{excellentPercentage}, muitiple_score = #{muitipleScore}, exam_id = #{examId}, muitiple_rank = #{muitipleRank}, average_rank = #{averageRank},
where exam_statistics_id = #{examStatisticsId}
