mapper.select:


     and exam_id = #{examId}
     and grade = #{grade}
     and classes = #{classes}
     and exam_number = #{examNumber}
     and subject = #{subject}
     and exam_name like concat('%', #{exams.examName}, '%')

order by score_id desc

SELECT grade,classes,`subject`,COUNT(score) AS `exam_numbers`,SUM(score) AS `total_score` FROM exam_student_scores

     and exam_id = #{examId}
     and grade = #{grade}
     and classes = #{classes}
     and subject = #{subject}

SELECT count(*) FROM
(
    SELECT exam_number

            SUM(IF(`subject`='语文',score,0)) AS chinese


            SUM(IF(`subject`='数学',score,0)) AS maths


            SUM(IF(`subject`='英语',score,0)) AS english

    FROM `exam_student_scores`

             and exam_id = #{examId}
             and grade = #{grade}
             and classes = #{classes}

    GROUP BY exam_number
) AS a

    and chinese >= #{chineseLine}
    and maths >= #{mathsLine}
    and english >= #{englishLine}

SELECT count(score) FROM exam_student_scores

     and exam_id = #{examId}
     and score >= #{underLine}
     and grade = #{grade}
     and classes = #{classes}
     and subject = #{subject}

SELECT
COUNT(*) AS exam_total_numbers,
SUM( score ) AS grade_total_scores,
ROUND( AVG( score ), 2 ) AS grade_average_score,
CONCAT(
ROUND((
SELECT
COUNT(*)
FROM
exam_student_scores
WHERE
exam_id = #{examId}
AND grade = #{grade}
AND `subject` = #{subject}
AND score >= 60
) / ( SELECT COUNT(*) FROM exam_student_scores WHERE exam_id = #{examId} AND grade = #{grade} AND `subject` = #{subject} ) * 100,
2
),
"%"
) AS grade_qualified_percentage,
CONCAT(
ROUND((
SELECT
COUNT(*)
FROM
exam_student_scores
WHERE
exam_id = #{examId}
AND grade = #{grade}
AND `subject` = #{subject}
AND score >= #{score}
) / ( SELECT COUNT(*) FROM exam_student_scores WHERE exam_id = #{examId} AND grade = #{grade} AND `subject` = #{subject} ) * 100,
2
),
"%"
) AS grade_excellent_percentage,
( SELECT COUNT(*) FROM exam_student_scores WHERE exam_id = #{examId} AND grade = #{grade} AND `subject` = #{subject} AND score < 60 ) AS grade_unqualified_numbers,
grade,subject
FROM
exam_student_scores a
WHERE
exam_id = #{examId}
AND grade = #{grade}
AND `subject` = #{subject}

mapper.update:
update exam_student_scores
grade = #{grade}, classes = #{classes}, exam_number = #{examNumber}, subject = #{subject}, score = #{score}, create_time = #{createTime}, create_by = #{createBy}, update_time = #{updateTime}, update_by = #{updateBy}, exam_id = #{examId},
where score_id = #{scoreId}
